import React, { useEffect, useState } from 'react';
import Gift from './Gift';
import './App.css';

function App() {
    const [coupon, setCoupon] = useState(null);

    useEffect(() => {
        fetch('/api/coupon')
            .then((res) => res.json())
            .then((data) => setCoupon(data.coupon))
            .catch(() => setCoupon(null));
    }, []);

    const unlockCoupon = () => {
        fetch('/api/unlock', { method: 'POST' })
            .then((res) => res.json())
            .then((data) => setCoupon(data.coupon))
            .catch(console.error);
    };

    return (
        <div className="App">
            {coupon ? (
                coupon.unlocked ? (
                    <div className="coupon">
                        <h1>{coupon.text}</h1>
                    </div>
                ) : (
                    <Gift onClick={unlockCoupon} />
                )
            ) : (
                <p>No hay cup√≥n disponible hoy.</p>
            )}
        </div>
    );
}

export default App;
