import React, { useEffect, useState } from 'react';
import Gift from './Gift';
import './App.css';

function App() {
    const [coupon, setCoupon] = useState(null); // Cup贸n de hoy
    const [unlockedCoupons, setUnlockedCoupons] = useState([]); // Lista de cupones desbloqueados
    const [showAllCoupons, setShowAllCoupons] = useState(false); // Mostrar todos los cupones desbloqueados

    // Cargar los datos iniciales
    useEffect(() => {
        // Obtener el cup贸n de hoy
        fetch('/api/coupon')
            .then((res) => res.json())
            .then((data) => setCoupon(data.coupon))
            .catch(() => setCoupon(null));

        // Obtener la lista de cupones desbloqueados
        fetch('/api/unlocked-coupons')
            .then((res) => res.json())
            .then((data) => setUnlockedCoupons(data.unlockedCoupons))
            .catch(() => setUnlockedCoupons([]));
    }, []);

    // Desbloquear el cup贸n de hoy
    const unlockCoupon = () => {
        fetch('/api/unlock', { method: 'POST' })
            .then((res) => res.json())
            .then((data) => {
                setCoupon(data.coupon);
                // Actualizar la lista de cupones desbloqueados
                fetch('/api/unlocked-coupons')
                    .then((res) => res.json())
                    .then((data) => setUnlockedCoupons(data.unlockedCoupons))
                    .catch(console.error);
            })
            .catch(console.error);
    };

    // Cambiar entre mostrar todos los cupones o el estado actual
    const toggleShowAllCoupons = () => {
        setShowAllCoupons(!showAllCoupons);
    };

    return (
        <div className="App">
            {showAllCoupons ? (
                // Mostrar el cup贸n de hoy o el regalo para desbloquear
                <div>
                    {coupon ? (
                        coupon.unlocked ? (
                            <div className="coupon">
                                <h1>隆Has desbloqueado un cup贸n de amistad!</h1>
                                <h2>{coupon.text} </h2>
                                <button onClick={toggleShowAllCoupons}>
                                    Ver todos los cupones desbloqueados
                                </button>
                            </div>
                        ) : (
                            <div>
                                <Gift onClick={unlockCoupon} />
                            </div>
                        )
                    ) : (
                        <p>No hay cup贸n disponible hoy.</p>
                    )}
                </div>
            ) : (
                // Mostrar todos los cupones desbloqueados
                <div className="unlocked-coupons">
                    <h1>Cupones desbloqueados:</h1>
                    <ul>
                        {unlockedCoupons.map((coupon, index) => (
                            <li key={index}>{coupon.text}</li>
                        ))}
                    </ul>
                    <button onClick={toggleShowAllCoupons}>Volver</button>
                </div>
            )}
        </div>
    );
}

export default App;
